[h: "SPELL PROPERTIES"]
[h: vCasterName  = json.get(macro.args, 'casterName')]
[h: vSpellName   = json.get(macro.args, 'spell')]
[h: vSpellLink   = macroLink(vSpellName, "Show a Spell@Lib:SpellLibrary", "none", vSpellName, "impersonated")]
[h: vDamage      = json.get(macro.args, 'damage')]
[h: vElement     = json.get(macro.args, 'type')]
[h: vIsAttack    = if( json.get(macro.args, 'save') == "ac", 1, 0)]
[h: vPlus        = json.get(macro.args, 'plus')]
[h: vPlusMod     = if(vPlus == "mod", 1, 0)]
[h, if(currentToken() == ""): vPlusMod = 0]

[h: vHeal        = if(vElement == "heal", 1, 0)]

[h: vScaling     = json.get(macro.args, 'scaling')]
[h: vLevels      = json.get(macro.args, 'levels')]

[h: vVerb     = if(vHeal, "heals", "inflicts")]
[h: vQuantity = if(vHeal, "hit points", vElement + " damage")]

[h, if(indexOf(vLevels, "-") == 1), CODE:{
	[vStart = substring(vLevels, 0, 1)]
	[vEnd   = substring(vLevels, 2, 3)]
	[vLevels = ""]
	[for (vI, vStart, (vEnd+1), 1): vLevels = listAppend(vLevels, vI) ]
};{}]

[h, if (vLevels != ""), CODE:{
	[resp = input("vLevel|"+vLevels+"|Select Higher Level|LIST|SELECT=0 DELIMITER=,")][abort(resp)]
};{
	[vLevel = 0]
}]

[r: vSpellLink] [r:vVerb] [t: vRoll = eval(string(vDamage))] ([r: vDamage])

[if(vLevel > 0), CODE:{ 
	  [h: vId    = strfind(vScaling, "([0-9])(d[0-9]+)")]
	  [h: vExtra = getGroup(vId, 1, 1)*vLevel + getGroup(vId, 1,2)]
	  [h: vTextExtra = "<span title='Upcasted to level " + listGet(vLevels, vLevel) + "'>" + vExtra + "</span>"]
	+ [t: vExtraDamage = eval(vExtra)] ([r: vTextExtra])
};{
	  [h: vExtraDamage = 0]	
}]

[if (vPlusMod == 1), CODE:{
	[h: vCasterId      = json.get(macro.args, "casterId")]
	[h: vCastStat      = substring(json.get(macro.args, 'stat'), 0, 3)]
	[h: vCastStatMod   = getProperty(vCastStat + 'mod', vCasterId)]
	+ [r: vCastStatMod] = <b>[r: vTotal = vRoll + vExtraDamage + vCastStatMod]</b>
};{
	[if (isNumber(vPlus)), CODE:{
		+ [r: vPlus] = <b>[r: vTotal = vRoll + vExtraDamage + vPlus]</b>
	};{
		= <b>[r: vTotal = vRoll + vExtraDamage]</b>
	}]
}]
[r: vQuantity][r, if(vHeal == 0): " (half: " + floor(vTotal/2) + if(vIsAttack == 0, ").", ""); "."]

[h: "If attack, display the damage for a critical hit."]
[if (vIsAttack == 1), CODE:{
	, crit: [r:vTotal] +  [t: vRoll = eval(string(vDamage))] ([r: vDamage])
	[if (vLevel > 0), CODE:{
		+ [t: vExtraDamage = eval(vExtra)] ([r: vTextExtra])
	};{}]
	= [r: vTotalCrit = vTotal + vRoll + vExtraDamage], crit half: [r: floor(vTotalCrit/2)]).
};{}]

[h: vSpell  = json.get(macro.args, "spell")]
[h: vAction = json.get(macro.args, "action")]
[h: vStat = json.get(macro.args, "stat")]
[h: vSpells = getProperty("Spells")]
[h: vMacroName = 'Spellbook']

[if(vAction == 'forget'), CODE:{
	[h: vSpells = json.remove(vSpells, vSpell)]
	Forgot [r:vSpell].
};{}]

[if(vAction == 'learn'), CODE:{
	[h: jsonSpell = json.set("{}", "stat", json.get(macro.args, "stat"), "prepared", "no")]
	[h: vSpells = json.set(vSpells, vSpell, jsonSpell)]
	Learned [r:vSpell].

	[h: vAddBookMacro = 0]
	[h, if(!hasMacro(vMacroName)), CODE:{
		[vOption = getLibProperty("BookMacroOption")]
		[if (vOption == "" || vOption == "On Learning First Spell"): vAddBookMacro = if(json.length(vSpells) == 1, 1, 0)]
		[if (vOption == "On Learning Any Spell"): vAddBookMacro = 1]
	};{}]

	[h, if (vAddBookMacro), CODE:{
		[vCommand = '[macro("Spell Library@Lib:SpellLibrary"):"cast"]']
		[vTooltip=  'Consults the spell book of the character. To add a spell to the spellbook, click on the Spell Library macro.']
		[vProp    = json.set("{}", "color", "blue", "fontColor", "white", "playerEditable", 0, "tooltip", vTooltip, "group", "Spells")]
		[createMacro(vMacroName, vCommand, vProp, 'json')]
	};{}]
};{}]


[if(vAction == 'prepare'), CODE:{
	[h: jsonSpell = json.get(vSpells, vSpell)]
	[h: jsonSpell = json.set( jsonSpell, 'prepared', 'yes')]
	[h: vSpells = json.set(vSpells, vSpell, jsonSpell)]
	Prepared [r:vSpell].
};{}]

[if(vAction == 'unprepare'), CODE:{
	[h: jsonSpell = json.get(vSpells, vSpell)]
	[h: jsonSpell = json.set( jsonSpell, 'prepared', 'no')]
	[h: vSpells = json.set(vSpells, vSpell, jsonSpell)]
	Unprepared [r:vSpell].
};{}]

[if(vAction == 'changeStat'), CODE:{
	[h: jsonSpell = json.get(vSpells, vSpell)]
	[if (json.isEmpty(jsonSpell) == 0), CODE:{
		[h: jsonSpell = json.set( jsonSpell, 'stat', vStat)]
		[h: vSpells = json.set(vSpells, vSpell, jsonSpell)]
		Changed [r:vSpell] Spellcasting Ability to [r:vStat].
	}]
};{}]


[h: setProperty("Spells", vSpells)]

[h, if(macro.args == ""), CODE:{

	[macro.return = "Charisma"]

}; {
	[switchToken(macro.args)]
	[vInt = getProperty("IntMod")][h: vWis = getProperty("WisMod")][h: vCha = getProperty("ChaMod")]

	[if(isNumber(vInt) == 0) : vInt = -5][h, if(isNumber(vWis) == 0) : vWis = -5][h, if(isNumber(vCha) == 0) : vCha = -5]

	                          [vMaxStat = 'Charisma']     [vMax = vCha]
	[if (vInt > vMax), CODE:{ [vMaxStat = 'Intelligence'] [vMax = vInt] }]
	[if (vWis > vMax), CODE:{ [vMaxStat = 'Wisdom'      ] [vMax = vWis] }]

	[macro.return = vMaxStat]
}]

[h: vSpell = macro.args]

[h, if(vSpell == ""): vSpell = 'Fireball']

[h: vLib   = 'Lib:SpellLibrary']
[h: vMap   = getTokenMap(vLib)]
[h: vIndex = getMacroIndexes('displaySpell', ',', vLib, vMap)]
[h: vMacro = getMacroCommand( listGet(vIndex, 0), vLib, vMap)]

[macro("getDefaultAbility@Lib:SpellLibrary"):""]
[h: vDefaultStat = macro.return]
[h: vDefaultDC = 15]

[macro("getCasterToken@Lib:SpellLibrary"):""]
[h: vCurrentId = macro.return]

[h: "If valid token, take known spell list"]
[h, if(vCurrentId != ""), CODE:{
	[switchToken(vCurrentId)]
	[vKnown = getProperty("Spells")]
	[vName  = getName()]
	[if(json.isEmpty(vKnown)): vKnown = "{}"]
	[macro("getDefaultAbility@Lib:SpellLibrary"):vCurrentId]
	[vDefaultStat = macro.return]
};{
	[vKnown = "{}"]
	[vName = ""]
}]

[h: vTitle = capitalize(vSpell) + " Spell" + if(vName != "", " - " + vName, "")] 

[frame5(vTitle, "width=550; height=450"):{
<head><link rel='stylesheet' type='text/css' href='CSS@[r:vLib]'></link></head>

[macro("formElements@" + vLib):json.set("{}", "Name", vName, "CurrentId", vCurrentId, "ShowList", 0, "Cast", if(vCurrentId == "", 0, 1))]

[r: vMacro]

